function Fav(id) {
    let m = localStorage.getItem("wishlist")
    let arr = JSON.parse(m)
    let fav_icon = document.getElementById("favorate" + id);
    console.log("g", fav_icon);
    if (m == null) {
        let wishlist = [];
        for (data of items) {
            if (id == data.id) {
                wishlist.push(data);
                fav_icon.style.color = "red";
                localStorage.setItem("wishlist", JSON.stringify(wishlist));
            }
        }
    }
    else {

        for (data of items) {
            if (id == data.id) {
                const new_array = arr.filter((item) => item.id == id);
                if (new_array.length == 0) {
                    arr.push(data);
                    fav_icon.style.color = "red";
                    localStorage.setItem("wishlist", JSON.stringify(arr));
                }
                else {
                    fav_icon.style.color = "#929090";
                    remove_wishlist_element(id);
                    console.log("++++++++++++", id);
                }
            }
        }
    }
}
function remove_wishlist_element(id) {
    let wish = localStorage.getItem("wishlist")
    let wish_arr = JSON.parse(wish)
    for (i = 0; i < wish_arr.length; i++) {
        if (wish_arr[i].id == id) {
            wish_arr.splice(i, 1);
        }
    }
    localStorage.setItem("wishlist", JSON.stringify(wish_arr));
}

