function Register(name, email, pass, phone) {

    inputDetails = {
        "Name": name,
        "Email": email,
        "password": pass,
        "phone": phone
    }

    let UD = localStorage.getItem("userData")
    const arr = JSON.parse(UD)

    if (UD == null) {
        let userData = [];
        userData.push(inputDetails);
        localStorage.setItem("userData", JSON.stringify(userData));
    }
    else {
        const new_array = arr.filter((userData) => userData.Email == email);
        if (new_array.length == 0) {
            arr.push(inputDetails);
        }
        localStorage.setItem("userData", JSON.stringify(arr));
    }
}

