function cart_page(id) {
    let cart = localStorage.getItem("cart_collection_storage")
    const cart_arr = JSON.parse(cart)
    let conf_id;
    if (id.substr(0, 5) == "minus") {
        conf_id = id.substr(5);
    }
    else
        if (id.substr(0, 4) == "plus") {
            conf_id = id.substr(4);
        }
        else {
            conf_id = id;
        }

    if (cart == null) {
        let cart_list = [];
        for (data of items) {
            if (conf_id == data.id) {
                cart_list.push(data);
                localStorage.setItem("cart_collection_storage", JSON.stringify(cart_list));
            }
        }
    }
    else {

        for (data of items) {
            if (conf_id == data.id) {

                const new_array = cart_arr.filter((item) => item.id == conf_id);
                if (new_array.length == 0) {
                    cart_arr.push(data);
                }
                else {
                    for (var i = 0; i < cart_arr.length; i++) {
                        if (("plus" + cart_arr[i].id) == id) {
                            cart_arr[i].quantity += 1;
                        }
                        else
                            if (("minus" + cart_arr[i].id) == id) {

                                if (cart_arr[i].quantity == 1) {
                                    cart_arr.splice(i, 1);
                                    bill();
                                    //removing from cart if quantity gets zero
                                }
                                else {
                                    cart_arr[i].quantity -= 1;
                                }
                            }
                            else
                                if (cart_arr[i].id == conf_id) { cart_arr[i].quantity += 1; }
                    }
                }
                localStorage.setItem("cart_collection_storage", JSON.stringify(cart_arr));
            }
        }
    }

    if (id.substr(0, 5) == 'minus' || id.substr(0, 4) == 'plus') {
        cart_input_page()
        bill();
    }

    show_cart_element_count()
}

function show_cart_element_count() {
    let cart = localStorage.getItem("cart_collection_storage")
    const cart_arr = JSON.parse(cart)
    x = document.getElementById("cart_count")
    if (cart != null) {
        if (cart_arr.length > 0) { x.innerHTML = cart_arr.length; }
        else { x.innerHTML = ""; }

    }
}

function remove_cart_element(id) {
    let cart = localStorage.getItem("cart_collection_storage")
    const cart_arr = JSON.parse(cart)

    for (i = 0; i < cart_arr.length; i++) {
        if (cart_arr[i].id == id) {
            cart_arr.splice(i, 1);
        }

    }
    localStorage.setItem("cart_collection_storage", JSON.stringify(cart_arr));
    cart_input_page()
    show_cart_element_count();
    bill();
}
